import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Autocomplete from '@mui/material/Autocomplete';
import '../styles/user.css'
import Avatar from '@mui/material/Avatar';

function stringToColor(string) {
  let hash = 0;
  let i;

  /* eslint-disable no-bitwise */
  for (i = 0; i < string.length; i += 1) {
    hash = string.charCodeAt(i) + ((hash << 5) - hash);
  }

  let color = '#';

  for (i = 0; i < 3; i += 1) {
    const value = (hash >> (i * 8)) & 0xff;
    color += `00${value.toString(16)}`.substr(-2);
  }
  /* eslint-enable no-bitwise */

  return color;
}

function stringAvatar(name) {
  return {
    sx: {
      bgcolor: stringToColor(name),
      width: 80,
      height: 80,
      margin: 'auto',
      fontSize: 32
    },
    children: `${name.split(' ')[0][0]}${name.split(' ')[1][0]}`,
  };
}

export default function Home() {
  let { id } = useParams()
  useEffect(() => {
    getData();
  }, []);
  const [user, setUser] = useState({});
  const [groups, setGroup] = useState([]);
  const [groupsID, setGroupID] = useState({});

  const getData = async () => {
    let response = await axios.get(`http://${window.location.hostname}:8080/api/user/${id}`)
    console.log(response)
    setUser(response.data)
    response = await axios.get(`http://${window.location.hostname}:8080/api/group`)

    let group_names = [];
    let groupIDs = {}
    response.data.forEach(element => {
      group_names.push(element.name)
      groupIDs[element.name] = element.id
    });
    setGroupID(groupIDs)
    setGroup(group_names)
  }
  const [open, setOpen] = useState(false);
  const [open1, setOpen1] = useState(false);
  const handleClickOpen1 = () => {
    setOpen1(true);
  };

  const handleClose1 = () => {
    setOpen1(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const handleCreate = async (groupName) => {
    setOpen(false);
    let xd = { name: groupName }
    console.log(xd)
    const response = await axios.post(
      `http://${window.location.hostname}:8080/api/group`,
      xd,
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(response)
    handleClose()
  };
  const handleAssign = async (groupName) => {
    setOpen(false);
    let xd = user
    xd.groupID = groupsID[groupName]
    console.log(xd)
    const response = await axios.put(
      `http://${window.location.hostname}:8080/api/user`,
      xd,
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(response)
    handleClose1()
  };
  return (
    <div className="container">
      <div className="left-panel">
        <div>
          <Avatar {...stringAvatar(user.firstName + ' ' + user.lastName)} />
          {user.firstName}<br />
          {user.lastName} <br />
        </div>
        <br/>
      </div>
      <div className="groups">
          <h3>Group management</h3>
          <TextField
              autoFocus
              id="name"
              label="Group name"
              type="name"
              variant="standard"
            />
            <Button variant="outlined" onClick={() => handleCreate(document.getElementById('name').value)}>Create Group</Button>
        <Button variant="outlined" onClick={handleClickOpen1}>
          Assign to Group
        </Button>
        <Dialog open={open1} onClose={handleClose1}>
          <DialogTitle>Assign to group</DialogTitle>
          <DialogContent>
            <DialogContentText>
              In order to manage group you need to to choose group or create a new one
            </DialogContentText>
            <Autocomplete
              disablePortal
              id="assign_group"
              options={groups}
              limitTags="0"
              sx={{ width: 300 }}
              renderInput={(params) => <TextField {...params} label="Group" />}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose1}>Cancel</Button>
            <Button onClick={() => handleAssign(document.getElementById('assign_group').value)}>Assign</Button>
          </DialogActions>
        </Dialog>
      </div>
      <div className="students">
          <h3 style={{textAlign: 'center'}}>Your students</h3>
      </div>
      <div className="attendances">
          <h3>Attendance list</h3>
      </div>
  </div>
  )
}
