import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Box from '@mui/material/Box';
import Attendances from './Attendances';
export default function Home() {
  let { id } = useParams()
  useEffect(() => {
    getData();
  }, []);
  const [user, setUser] = useState({});


  const getData = async () => {
    let response = await axios.get(`http://${window.location.hostname}:8080/api/user/${id}`)
    console.log(response)
    setUser(response.data)
    response = await axios.get(`http://${window.location.hostname}:8080/api/group`)
  }

  return (
    <div>
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        First name: {user.firstName}<br /> <br />
        Last name: {user.lastName} <br /> <br />
        Role: {user.role}<br /><br />
        Group: {user.groupID}<br /><br />
      </Box>
    </div>
  )
}
