import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Box from '@mui/material/Box';
import Autocomplete from '@mui/material/Autocomplete';

export default function Home() {
  let { id } = useParams()
  useEffect(() => {
    getData();
  }, []);
  const [user, setUser] = useState({});
  const [groups, setGroup] = useState([]);

  const getData = async () => {
    let response = await axios.get(`http://${window.location.hostname}:8080/api/user/${id}`)
    console.log(response)
    setUser(response.data)
    response = await axios.get(`http://${window.location.hostname}:8080/api/group`)

    let group_names = [];
    response.data.forEach(element => {
      group_names.push(element.name)
    });

    setGroup(group_names)
  }
  const [open, setOpen] = useState(false);
  const [open1, setOpen1] = useState(false);
  const handleClickOpen1 = () => {
    setOpen1(true);
  };

  const handleClose1 = () => {
    setOpen1(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const handleCreate = async (groupName) => {
    setOpen(false);
    let xd = { name: groupName }
    console.log(xd)
    const response = await axios.post(
      `http://${window.location.hostname}:8080/api/group`,
      xd,
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(response)
  };
  return (
    <div>
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        First name: {user.firstName}<br /> <br />
        Last name: {user.lastName} <br /> <br />
        Role: {user.role}<br /><br />
        <Button variant="outlined" onClick={handleClickOpen}>
          Create Group
        </Button>
        <Dialog open={open} onClose={handleClose}>
          <DialogTitle>Create group</DialogTitle>
          <DialogContent>
            <DialogContentText>
              In order to manage group you need to to choose group or create a new one
            </DialogContentText>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Group name"
              type="name"
              fullWidth
              variant="standard"
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={() => handleCreate(document.getElementById('name').value)}>Create</Button>
          </DialogActions>
        </Dialog>
        <Button variant="outlined" onClick={handleClickOpen1}>
          Assign to Group
        </Button>
        <Dialog open={open1} onClose={handleClose1}>
          <DialogTitle>Assign to group</DialogTitle>
          <DialogContent>
            <DialogContentText>
              In order to manage group you need to to choose group or create a new one
            </DialogContentText>
            <Autocomplete
              disablePortal
              id="combo-box-demo"
              options={groups}
              sx={{ width: 300 }}
              renderInput={(params) => <TextField {...params} label="Group" />}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose1}>Cancel</Button>
            <Button onClick={() => handleCreate(document.getElementById('name').value)}>Create</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </div>
  )
}
