import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import '../styles/student.css'

export default function Attendances(params) {
  let { id } = useParams()
  useEffect(() => {
    const currentDate = new Date()
    getData().then(() => {
      const rows = document.getElementsByClassName('attendance-entity')
      // console.log(rows)
      for (let item of rows) {
        let dates = item.getElementsByClassName('entity-header')[0].children[0].innerText.split('-')
        console.log(currentDate.toISOString().substr(0, currentDate.toISOString().length - 8))
      }
    })
  }, []);
  const [attendance, setAttendances] = useState([]);

  const getData = async () => {
    let response = await axios.get(`http://${window.location.hostname}:8080/api/user/attendance/${id}`)
    console.log(response)
    setAttendances(response.data)
  }
  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    let status = data.get('status')
    if (status === null)
      return
    let other = event.target.id.split('-')
    let att = {
      attendanceFrom: other[0],
      attendanceTo: other[1],
      attendanceStatus: status,
      nameOfSubject: other[2],
      id: parseInt(other[3]),
      studentID: parseInt(other[4])
    }
    const response = await axios.put(
      `http://${window.location.hostname}:8080/api/attendance`,
      att,
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(response)
  }

  return (
    <div>
      {attendance.map((element) => {
        return (
          <Box
            id={`${element.attendanceFrom}-${element.attendanceTo}-${element.nameOfSubject}-${element.id}-${element.studentID}`}
            className="attendance-entity"
            component="form"
            onSubmit={handleSubmit}
          >
            <CalendarTodayIcon style={{ transform: 'translateY(5px)' }} />
            <div className="entity-header">
              <span style={{ fontWeight: 700, paddingRight: 20 }}>{element.attendanceFrom} - {element.attendanceTo} </span> {element.nameOfSubject}
            </div>
            <FormControl component="fieldset" style={{ flexDirection: 'row', position: 'absolute', right: 20 }} >
              <RadioGroup row aria-label="status" name='status' defaultValue={element.attendanceStatus}>
                <FormControlLabel id="absent" value="absent" control={<Radio />} label="Absent" />
                <FormControlLabel id="late" value="late" control={<Radio />} label="Late" />
                <FormControlLabel id="present" value="present" control={<Radio />} label="Present" />
              </RadioGroup>
              <Button variant="contained" type='submit'>confirm</Button>
            </FormControl>
          </Box>
        )
      })}
    </div>
  )
}
