import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';

import '../styles/student.css'

export default function Attendances(params) {
  let { id } = useParams()
  useEffect(() => {
    getData();
  }, []);
  const [attendance, setAttendances] = useState([]);

  const getData = async () => {
    let response = await axios.get(`http://${window.location.hostname}:8080/api/user/attendance/${id}`)
    console.log(response)
    setAttendances(response.data)
  }
  const handleUpdateAttendance = async (student) => {
    student.attendanceStatus = 'present'
    console.log(document.getElementById(`${student.attendanceFrom}-${student.attendanceTo}-${student.nameOfSubject}`).value)
    console.log(student)
  }
  const handleSubmit = async (event) => {
    console.log('asdas')
    event.preventDefault();
    console.log(event)
    const data = new FormData(event.currentTarget);
    console.log(data.get('status'))
  }
  return (
    <div>
      {attendance.map((element) => {
        return (
          <Box id = {`${student.attendanceFrom}-${student.attendanceTo}-${student.nameOfSubject}`} className="attendance-entity" component="form" onSubmit={handleSubmit}>
            <div className="entity-header">
              <span style={{ fontWeight: 700, paddingRight: 20 }}>{element.attendanceFrom} - {element.attendanceTo} </span> {element.nameOfSubject}
            </div>
            <FormControl component="fieldset" style={{ flexDirection: 'row', position: 'absolute', right: 20 }} >
              <RadioGroup row aria-label="status" name='status'>
                <FormControlLabel id="absent" value="absent" control={<Radio />} label="Absent" />
                <FormControlLabel id="late" value="late" control={<Radio />} label="Late" />
                <FormControlLabel id="present" value="present" control={<Radio />} label="Present" />
              </RadioGroup>
              <Button variant="contained" type='submit' onC>confirm</Button>
            </FormControl>
          </Box>
        )
      })}
    </div>
  )
}
