import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import '../styles/user.css'
import Avatar from '@mui/material/Avatar';

function stringToColor(string) {
  let hash = 0;
  let i;

  /* eslint-disable no-bitwise */
  for (i = 0; i < string.length; i += 1) {
    hash = string.charCodeAt(i) + ((hash << 5) - hash);
  }

  let color = '#';

  for (i = 0; i < 3; i += 1) {
    const value = (hash >> (i * 8)) & 0xff;
    color += `00${value.toString(16)}`.substr(-2);
  }
  /* eslint-enable no-bitwise */

  return color;
}

function stringAvatar(name) {
  return {
    sx: {
      bgcolor: stringToColor(name),
      width: 80,
      height: 80,
      margin: 'auto',
      fontSize: 32
    },
    children: `${name.split(' ')[0][0]}${name.split(' ')[1][0]}`,
  };
}

export default function Home() {
  let { id } = useParams()
  useEffect(() => {
    getData();
  }, []);
  const [user, setUser] = useState({});
  const [groups, setGroup] = useState([]);
  const [groupsID, setGroupID] = useState({});
  const [students, setStudents] = useState([]);

  const getData = async () => {
    let response = await axios.get(`http://${window.location.hostname}:8080/api/user/${id}`)
    console.log(response)
    setUser(response.data)
    let idOfGroup = response.data.groupID
    response = await axios.get(`http://${window.location.hostname}:8080/api/group`)

    let group_names = [];
    let groupIDs = {}
    response.data.forEach(element => {
      group_names.push(element.name)
      groupIDs[element.name] = element.id
    });
    setGroupID(groupIDs)
    setGroup(group_names)
    response = await axios.get(`http://${window.location.hostname}:8080/api/user/group/${idOfGroup}`)
    setStudents(response.data)
  }

  const handleCreate = async (groupName) => {
    let xd = { name: groupName }
    console.log(xd)
    const response = await axios.post(
      `http://${window.location.hostname}:8080/api/group`,
      xd,
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(response)
    alert(`Group: ${response.data.name} created!`)
    getData()
  };
  const handleAssign = async (groupName) => {
    let xd = user
    xd.groupID = groupsID[groupName]
    console.log(xd)
    const response = await axios.put(
      `http://${window.location.hostname}:8080/api/user`,
      xd,
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(response)
    getData()
  };

  const handleDelete = async (student) => {
    student.groupID = null
    console.log(student)
    if (window.confirm(`Student ${student.firstName} ${student.lastName} will be deleted!`)){
      const response = await axios.put(
        `http://${window.location.hostname}:8080/api/user`,
        student,
        { headers: { "Content-Type": "application/json" } }
      );
      console.log(response)
    }
    getData()
  }
  return (
    <div className="container">
      <div className="left-panel">
        <div>
          <Avatar {...stringAvatar(user.firstName + ' ' + user.lastName)} />
          {user.firstName} {user.lastName} <br />
          <span style={{fontSize:32, fontWeight: 300}}> {groups[user.groupID - 1]} </span>
        </div>
        <br/>
      </div>
      <div className="groups">
          <h3>Group management</h3>
          <TextField
              autoFocus
              id="name"
              label="Group name"
              type="name"
              variant="standard"
              style={{margin: 10, verticalAlign: 'inherit', width: 300}}
            />
            <Button variant="contained" onClick={() => handleCreate(document.getElementById('name').value)}>Create Group</Button>
            <br />
            <div style={{float: 'left'}}>
              <Autocomplete
                id="assign_group"
                options={groups}
                disableClearable
                renderInput={(params) => (
                  <TextField {...params} label="Group name" variant="standard" />
                )}
                style={{margin: 10, verticalAlign: 'inherit', width: 300}}
              />
            </div>
            <Button style={{float: 'left', margin: '23px 0'}} variant="outlined" onClick={() => handleAssign(document.getElementById('assign_group').value)}>Change group</Button>
      </div>
      <div className="students">
          <h3 style={{textAlign: 'center'}}>Your students</h3>
          {students.map((element) => {
              if (element.role === 'student')
                return (
                  <div className='student'>
                    {element.firstName} {element.lastName} 
                      <Button 
                        style={{position: 'absolute', right: 5}} 
                        variant="outlined"
                        color="error"
                        onClick={() => handleDelete(element)}
                      > 
                        Delete 
                      </Button>
                  </div>
                )
                else return null
            })}
            <div style={{float: 'left'}}>
              <Autocomplete
                id="assign_group"
                options={groups}
                disableClearable
                renderInput={(params) => (
                  <TextField {...params} label="Group name" variant="standard" />
                )}
                style={{margin: 10, verticalAlign: 'inherit', width: 260}}
              />
            </div>
            <Button style={{float: 'left', margin: '23px 0'}} variant="outlined" onClick={() => handleAssign(document.getElementById('assign_group').value)}>add student</Button>
      </div>
      <div className="attendances">
          <h3>Attendance list</h3>

      </div>
  </div>
  )
}
