import { React, useEffect, useState } from 'react'
import axios from "axios";
import { useParams } from 'react-router-dom';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';

export default function Attendances() {
    let { id } = useParams()
    useEffect(() => {
      getData();
    }, []);
    const [attendance, setAttendances] = useState([]);

    const getData = async () => {
      let response = await axios.get(`http://${window.location.hostname}:8080/api/user/attendance/${id}`)
      console.log(response)
      setAttendances(response.data)

    }
    return (
        <div>
            {attendance.map((element) => {
              return (
                <div>{element.attendanceFrom} - {element.attendanceTo}<br />
                    <FormControl component="fieldset">
                      <FormLabel component="legend">Status</FormLabel>
                      <RadioGroup row aria-label="gender" name="row-radio-buttons-group">
                        <FormControlLabel value="absent" control={<Radio />} label="Absent" />
                        <FormControlLabel value="late" control={<Radio />} label="Late" />
                        <FormControlLabel value="present" control={<Radio />} label="Present" />
                      </RadioGroup>
                    </FormControl>
                </div>

              )
            })}
        </div>
    )
}
